"""
YOLO Logo Detector Training Script
Trains YOLOv8 model on the collected logo dataset
"""

import sys
sys.path.append(r"C:\Users\lumaa\AppData\Roaming\Python\Python313\site-packages")

from ultralytics import YOLO
from pathlib import Path
import yaml

def train_model():
    """Train YOLOv8 model on logo dataset"""
    print("ğŸš€ Starting YOLO training...\n")
    
    # Use the config generated by the dataset generator
    dataset_yaml = Path('../data/logo_dataset/data.yaml').absolute()
    
    if not dataset_yaml.exists():
        print(f"âŒ Error: {dataset_yaml} not found. Run generate_dataset.py first.")
        return

    print(f"ğŸ“„ Using dataset config: {dataset_yaml}")
    
    # Load pretrained YOLOv8 nano model
    model = YOLO('yolov8n.pt')
    
    # Training parameters
    results = model.train(
        data=str(dataset_yaml),
        epochs=50,              # Number of training epochs
        imgsz=640,              # Image size
        batch=16,               # Batch size (adjust based on your GPU/RAM)
        patience=10,            # Early stopping patience
        save=True,              # Save checkpoints
        project='logo_detection',
        name='phishguard_v2',
        device='cpu',           # Use 'cuda' if you have GPU
        verbose=True,
        plots=True,             # Generate training plots
    )
    
    print("\nâœ… Training complete!")
    print(f"ğŸ“Š Results saved to: logo_detection/phishguard_v2/")
    
    # Validate the model
    print("\nğŸ” Validating model...")
    metrics = model.val()
    
    print(f"\nğŸ“ˆ Performance Metrics:")
    print(f"  mAP50: {metrics.box.map50:.3f}")
    print(f"  mAP50-95: {metrics.box.map:.3f}")
    print(f"  Precision: {metrics.box.mp:.3f}")
    print(f"  Recall: {metrics.box.mr:.3f}")
    
    # Copy best model to models directory
    best_model_path = Path('logo_detection/phishguard_v2/weights/best.pt')
    if best_model_path.exists():
        target_path = Path('../models/logo_detector_v2.pt')
        target_path.parent.mkdir(parents=True, exist_ok=True)
        
        import shutil
        shutil.copy(best_model_path, target_path)
        print(f"\nâœ… Model saved to: {target_path}")
    
    return results

if __name__ == "__main__":
    print("=" * 60)
    print("YOLO Logo Detector Training")
    print("=" * 60)
    print("\nâš ï¸  Prerequisites:")
    print("  1. Dataset images in: data/logo_dataset/images/train/")
    print("  2. YOLO labels in: data/logo_dataset/labels/train/")
    print("  3. Validation set in: data/logo_dataset/images/val/")
    print("\n")
    
    input("Press Enter to start training (or Ctrl+C to cancel)...")
    
    try:
        train_model()
        print("\nğŸ‰ All done! Your model is ready to use.")
        print("\nğŸ¯ Next Steps:")
        print("  1. Update yolo_detector.py to use the new model")
        print("  2. Test with: python test_logo_detection.py")
        print("  3. Reload the extension and try scanning!")
    except KeyboardInterrupt:
        print("\n\nâŒ Training cancelled by user")
    except Exception as e:
        print(f"\n\nâŒ Error during training: {e}")
        print("ğŸ’¡ Tip: Make sure your dataset is properly organized")
